import styled from 'styled-components';

import { ArrowRight } from 'styled-icons/bootstrap';
import { UserPlus } from 'styled-icons/boxicons-regular';
import { Youtube } from '@styled-icons/feather';
import { FacebookBox } from '@styled-icons/remix-line';


export const Container = styled.div`
    display: grid;

    grid-template-columns: 1fr 1fr;
    grid-template-rows: auto 3rem;
    grid-template-areas:
        'Main Hero'
        'Footer Footer';

    height: 100vh;
`;

export const Main = styled.main`
    display: flex;

    grid-area: Main;

    margin: 8rem 5rem 0 5rem;

    flex-direction: column;
    justify-content: space-between;

    div:first-child > div {
        display: flex;

        img {
            width: 4.5rem;
            margin-right: 1rem;
        }
    }

    span {
        font-size: 24px;
        color: #949494;
    }


`;

export const Controllers = styled.div`
    display: flex;
    margin-bottom: 8rem;
`;

export const Title = styled.div`
    font-size: 3.5rem;
    /* text-shadow: 1px 1px 1px rgba(0, 0, 0, 0.100); */

`;

export const Line = styled.div`
    background-color: gray;
    height: 1px;
    margin-top: 1rem;
    box-shadow: 1px 1px 1px rgba(0, 0, 0, 0.100);
`;

export const LoginButton = styled.button`
    display: flex;
    width: 15rem;
    font-size: 1.8rem;
    justify-content: center;
    padding: 1rem;
    border: 0;
    border-radius: .2rem;
    background-color: #B266FF;
    color: white;

    margin-right: 3rem;

    cursor: pointer;
    transition: .2s;

    &:hover {
        width: 17rem;
    }

    box-shadow: 0px 0px 5px 10px rgba(0, 0, 0, 0.020);
`;

export const RegisterButton = styled.button`
    display: flex;
    width: 15rem;
    font-size: 1.8rem;
    justify-content: center;
    padding: 1rem;
    border: 0;
    border-bottom: 2px solid #B266FF;
    background-color: transparent;

    cursor: pointer;
    transition: .2s;

    &:hover {
        width: 17rem;
    }

    box-shadow: 0px 10px 0px rgba(0, 0, 0, 0.020);
`;

export const ArrowRightIcon = styled(ArrowRight)`
    width: 2.3rem;
    margin-left: .6rem;
`;

export const RegisterIcon = styled(UserPlus)`
    width: 2.3rem;
    margin-left: .6rem;
`;

export const Footer = styled.footer`
    display: flex;

    grid-area: Footer;
    height: 100%;

    background-color: #0a0a0a;

`;
