import styled from 'styled-components';

export const Container = styled.div`
    display: grid;

    grid-template-columns: 1fr 1fr;
    grid-template-rows: auto 6rem;
    grid-template-areas:
        'Main Hero'
        'Footer Footer';
`;

export const Main = styled.div`

    img {
        max-width: 3rem;
        max-height: 32px;
    }
`;

export const Title = styled.div``;
