import styles from 'styled-components';

export const LoginButton = styled.button`
    display: flex;
    width: 15rem;
    font-size: 1.8rem;
    justify-content: center;
    padding: 1rem;
    border: 0;
    border-radius: .2rem;
    background-color: #119549;
    color: white;

    margin-right: 3rem;

    cursor: pointer;
    transition: .2s;

    &:hover {
        width: 17rem;
        background-color: #10853e;
    }

    box-shadow: 0px 0px 5px 10px rgba(0, 0, 0, 0.020);
`;

export const RegisterButton = styled.button`
    display: flex;
    width: 15rem;
    font-size: 1.8rem;
    justify-content: center;
    padding: 1rem;
    border: 0;
    border-bottom: 2px solid #B266FF;
    background-color: transparent;

    cursor: pointer;
    transition: .2s;

    &:hover {
        width: 17rem;
    }

    box-shadow: 0px 10px 0px rgba(0, 0, 0, 0.020);
`;
