import styled from 'styled-components';

import { HelpCircle } from 'styled-icons/ionicons-outline'

export const Container = styled.div`
    grid-area: Header;

    display: flex;
    padding: .8rem;
    margin: 0 1rem;

    border-bottom: 1px solid gray;
`;

export const HelpIcon = styled(HelpCircle)`
    width: 40px;
    height: 40px;
`;
