import styled from 'styled-components';

import { ButtonModel } from '../utils/Buttons';

export const Container = styled.div`
    display: grid;

    max-width: 70vw;
    margin: 8rem auto;

    grid-template-columns: 1fr 2fr;

    background-color: white;

    padding: 2rem;
    border-radius: .2rem;

    box-shadow: 0px 0px 10px 6px rgba(0, 0, 0, 0.010);

    justify-content: center;
    text-align: center;

    div:first-child {
        img {


        }
    }


    div:last-child {

        display: flex;

        h2, input, select {
            margin-bottom: 2rem;
        }

        input {
            padding: 1rem;
            border-radius: .2rem;
            border: 1px solid black;
        }

        label {
            text-align: start;
            /* margin: 1rem; */
        }



        flex-direction: column;
    }
`;

export const LoginButton = styled(ButtonModel)`
    margin: auto;

    color: white;

    border-radius: .2rem;

    &:hover {
        width: 17rem;
        background-color: rgb(40, 40, 40);
    }
`;
