`;

export const Title = styled.div`
    font-size: 3.5rem;
    /* text-shadow: 1px 1px 1px rgba(0, 0, 0, 0.100); */

`;

export const LoginButton = styled.button`
    display: flex;
    width: 15rem;
    font-size: 1.8rem;
    justify-content: center;
    padding: 1rem;
