`;

export const ArrowRightIcon = styled(ArrowRight)`
    width: 2.3rem;
    margin-left: .6rem;
`;

export const RegisterIcon = styled(UserPlus)`
    width: 2.3rem;
    margin-left: .6rem;
`;

export const Footer = styled.footer`
    position: absolute;
    bottom: 0;
    right: 0;
    left: 0;
