import Head from 'next/head';


const Login: React.FC = () => {

    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');

    const options = [
        { label: 'React', value: 'react' }
    ]

    const tiltOptions = {
        reverse: true,
        max: 35,
        glare: true,
        maxGlare: 1,
        perspective: 1000,
        easing: "cubic-bezier(.03,.98,.52,.99)",
        scale: 1,
        speed: 300,
        transition: true,
        axis: null,
        reset: true
    }

    const submitForm = (event: FormEvent) => {
